@args String name, String blankName, double age, List<String> names

Hello, @name

---- if/else ----
@if(age<8) {
  @age < 8
} else if(age<16) {
  @age < 16
} else {
  @age >= 16
}

---- for ----
@for(name: names) {
  @name
}

@for(name: names).join(',') {
  @name
}

@for(name: names) {
@name: [index=@name_index] [isFirst=@name_isFirst] [isLast=@name_isLast] [isOdd=@name_isOdd] [sep=@name_sep]
}
